import datetimeimport osimport shutilimport subprocessfrom datetime import datefrom time import sleepimport allure_commons.reporterimport pytestfrom jproperties import Propertiesfrom Cron.config_allure_report import allure_misc_configfrom Cron.cron_image import delete_imgfrom Cron.cron_video import delete_vidparent_dir = os.getcwd()date_time = datetime.datetime.now(datetime.timezone.utc).strftime('%m-%d-%H%M')date = datetime.datetime.now(datetime.timezone.utc).strftime('%m-%d')ALLURE_HOME = shutil.which("allure")ANSIBLE_HOME = shutil.which("ansible-playbook")DOCKER_PATH = os.path.join(os.getcwd(), 'Docker', 'docker.yml')def main():    """    :return:    1. This main will Delete the Old Images and videos if Present.    2. This main method will execute Pytest command to execute and run tests mentioned in the Pytest.ini files    3. This main method will generate allure report from the allure results    4. This main method will make some modification in the allure report at the end to generate custom allure report.    """    delete_img()    delete_vid()    allure_file_html_name_old = get_value_from_prop('allure_file_html_name')    allure_result_file = f'{parent_dir}/reports/allure_results-{date_time}'    allure_html_file = f'{parent_dir}/reports/html/allure_report-html-{date_time}'    update_prop_file(f'date_time', date_time)    update_prop_file(f'allure_results', allure_result_file)    update_prop_file(f'allure_file_html_name', allure_html_file)    pytest.main(['-c', f'{parent_dir}/Test_Cases/pytest.ini',                 f'--alluredir={allure_result_file}',                 f'--log-file={parent_dir}/Test_Cases/Test_output/Logs/pytest_logs_{date}.log'                    , '-v', f'--junitxml={parent_dir}/reports/xml/allure_results_xml_{date_time}.xml'])    if os.path.exists(allure_result_file):        if 'history' not in os.listdir(allure_result_file):            os.mkdir(f'{allure_result_file}/history')    copy_tree(src=allure_file_html_name_old, destination=allure_result_file)    sleep(2)    if ALLURE_HOME is None:        ALLURE = get_value_from_prop('allure_home')        try:            subprocess.call([f'{ALLURE}', 'generate', allure_result_file, '-o', allure_html_file, '--clean'])        except FileNotFoundError as e:            print(e)    else:        try:            subprocess.call([f'{ALLURE_HOME}', 'generate', allure_result_file, '-o', allure_html_file, '--clean'])        except FileNotFoundError as e:            print(e)    sleep(2)    allure_misc_config(allure_html_file)    # Automate Docker Image and Container Creation    # subprocess.call([f'{ANSIBLE_HOME}', f'{DOCKER_PATH}', '-e',    #                  f'ReportName={allure_html_file}', '-e', f'Rname=allure_report-html-{date_time}'])    # subprocess.run([f'{ALLURE_HOME}', 'open', allure_html_file, '--port', '4444'])def update_prop_file(key, val):    CONFIG_FILE_PATH_PROP = os.path.join(os.getcwd(), 'Config', 'Environment.properties')    prop_config = Properties()    with open(CONFIG_FILE_PATH_PROP, 'rb') as r_file:        prop_config.load(r_file)        data = prop_config.get(key).data        print(f'Current Data in the {key}:: {data} ===')    prop_config[key] = val    with open(CONFIG_FILE_PATH_PROP, 'wb') as w_file:        prop_config.store(w_file)        data = prop_config.get(key).data        print(f'New value of {key}:: {data}')def get_value_from_prop(key):    CONFIG_FILE_PATH_PROP = os.path.join(os.getcwd(), 'Config', 'Environment.properties')    with open(CONFIG_FILE_PATH_PROP, 'r') as file:        for line in file.readlines():            if key in line:                file_path = line.split('=')[1].strip()                return file_pathdef copy_tree(src, destination):    if os.path.exists(src):        root_dir = os.listdir(src)        for dirs in root_dir:            if dirs == 'history':                shutil.copytree(src=f'{src}/history', dst=f'{destination}/history', dirs_exist_ok=True)                breakif __name__ == '__main__':    main()